body {
    margin: 0px;
    padding: 0px;;
    font-family: Verdana, Geneva, Tahoma, sans-serif;
}

:root {
    --white-color:#fff;
    --pink-color:#fd5f87;
}

.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    position: fixed;
    width: 100%;
    top: 0px;
    height: 70px;
    background-color: var(--white-color);
    z-index: 1;
}

.navbar-left .title {
    padding: 8px 12px;
}

.container {
    margin-block-start: 70px;
}

.navbar-center {
    justify-content: center;
    font-size: 15px;
    font-weight: bold;
    word-spacing: 15px;
    align-self: center;
}

.down-arrow {
    padding-top: 1px;
    margin-inline: 3px;
}

.navigation {
    text-transform: uppercase;
}

.navigation:hover {
    color: var(--pink-color);
    cursor: pointer;
}

.navbar-right {
    display: flex;
    justify-content: space-between;
    padding: 8px 12px;
}

.navbar-right .top-nav-icon {
    width: 25px;
    height: 22px;
    padding: 14px 6px;
    margin-block-start: 2px;
}

.top-nav-icon:hover {
    color: var(--pink-color);
    cursor: pointer;

}

.strawberry-big {
    width: -webkit-fill-available;
}

.top-container {
    width: auto;
    display: flex;
    justify-content: space-start;
}

.large-left-square-1 {
    width: 49%;
}

.large-right-square-1 {
    width: 49%;
    height: auto;
    margin-block: 40px;
}

.right-rectangle-1 {
    width: 50%;
    height: auto;
}

.right-rectangle-2 {
    justify-content: space-evenly;
    width: 50%;
    height: 50%;
}

.small-ice {
    width: 17rem;
    height: 14.5rem;
    padding: 0.5rem;
}

.small-ice-row-1-col-1 {
    display: flex;
    flex-direction: column;
    border: 1px solid #ddd;
    border-radius: 3px;
    margin: 10px 8px;
    width: 47%;
    float: left;
}

.small-ice-row-1-col-2 {
    display: flex;
    flex-direction: column;
    border: 1px solid #ddd;
    border-radius: 3px;
    margin: 10px 8px;
    width: 47%;
    float: right;
}

.txt {
    text-align: center;
    background-color: #f5f5fb;
    padding-bottom: .7rem;
    font-weight: bold;
}

.price {
    color: var(--pink-color);
    font-weight: bold;
    opacity: .9;
}

.price:hover {
    cursor: pointer;
}

.old-price {
    text-decoration: line-through;
    opacity: .5;
}

.poster {
    background: url("/images/poster.jpeg");
    width: 100%;
    height: auto;
    background-size: 102.5% 100%;
    background-repeat: no-repeat;
}

.content {
    padding: 8rem 16rem;
    display: flex;
    flex-direction: column;
    align-content: center;
}

.content .title {
    font-size: 3rem;
    padding: 0rem 6rem;
    margin-top: 25px;
    margin-bottom: 5px;
    text-align: center;
}

.subtitle {
    padding-left: 3.5rem;
    text-align: center;
    font-size: 14px;
    color: #8b8798;
}

.bottom-top-container {
    background-color: var(--pink-color);
    opacity: .85;
    display: flex;
    justify-content: center;
    padding-block: 1rem;
}

.product {
    width: 150px;
}

.bottom-end-container {
    background-color: #28272d;
    color: #8b8798;
}

.footer {
    display: flex;
    justify-content: space-between;
    padding: 0px 20px 0px 100px;

}

.support {
    display: flex;
    justify-content: space-around;
    margin-left: 275px;
}

.options {
    margin-right: .5rem;
}

.bottom-container {
    margin-top: 30px;
}

.bottom-middle-container {
    background-color: #2c2a33;

}

.details {
    padding: 50px 100px;
}

.contact-details {
    display: flex;
    justify-content: space-between;
    color: #8b8798;

}

.company-name {
    text-transform: uppercase;
    color: var(--pink-color);
    font-weight: bold;
}

.nav-icon {
    border-radius: 50%;
    background-color: var(--white-color);
    color: #bc4950;
    text-align: center;
    width: 30px;
    height: 23px;
    margin-top: 5px;
    padding-block-start: 4px;
}

.nav {
    padding: 10px 0px;
}

.info {
    margin-left: 10px;
}

.description {
    width: 312px;
}

.time {
    margin-top: 5px;
}

.map {
    width: 262px;
    height: 194px;
}

.bottom-line {
    height: 3px;
    display: flex;
}

.line {
    margin: 0px;
    width: 30px;
    height: 1px;
    border-radius: 50px;
    border: 1px solid var(--pink-color);
    background-color: var(--pink-color);
}

.dot {
    height: 1px;
    width: 1px;
    margin: 0px;
    margin-left: 5px;
    border-radius: 50%;
    border: 1px solid var(--pink-color);
    background-color: var(--pink-color);
}

.direction {
    margin-top: 20px;
}

.hot {
    text-transform: uppercase;
    text-align: center;
    color: var(--white-color);
    font-size: 15px;
    background-color: var(--pink-color);
    padding: 6px 17px;
    border: none;
    border-radius: 30px;
    position: absolute;
    margin-left: 210px;
    margin-top: 10px;
    opacity: .9;
}

.sale-only {
    text-transform: uppercase;
    text-align: center;
    color: var(--white-color);
    font-size: 15px;
    background-color: #e0b86e;
    padding: 6px 15px;
    border: none;
    border-radius: 30px;
    position: absolute;
    margin-left: 210px;
    margin-top: 10px;
    opacity: .9;
}

.sale {
    text-transform: uppercase;
    text-align: center;
    color: var(--white-color);
    font-size: 15px;
    background-color: #e0b86e;
    padding: 6px 14px;
    border: none;
    border-radius: 30px;
    position: absolute;
    margin-left: 210px;
    margin-top: 50px;
    opacity: .9;

}

.count {
    text-align: center;
    color: var(--white-color);
    background-color: var(--pink-color);
    padding: 3px 7px;
    border: none;
    border-radius: 30px;
    position: absolute;
    margin-left: 54px;
    margin-top: 10px;
}

h2 {
    color: var(--white-color);
}

h3 {
    color: var(--white-color);
    text-transform: uppercase;
    margin-bottom: 3px;
    margin-top: 15px;
}

#bottom-icon {
    background: #434148;
    color: #36343b;
    text-align: center;
    width: 39px;
    height: 35px;
    border-radius: 50%;
    margin-top: 5px;
    padding-block-start: 4px;
}

#about {
    background-color: var(--pink-color);
    width: 168px;
    padding: 15px;
    border-radius: 5px;
    color: var(--white-color);
    text-transform: uppercase;
    align-self: center;
}

